import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useTab } from "./TabContext";
import axios from "axios";
import "./Admin.css";

const Admin = () => {
  const navigate = useNavigate();
  const { activeTab, setActiveTab } = useTab();
  const [diaryEntries, setDiaryEntries] = useState([]);
  const user_id = localStorage.getItem("user_id");
  const token = localStorage.getItem("token");

  return (
    <div className="table-calendar">
      {/* 탭 영역 */}
      <div className="tab-header">
        <button
          className={`tab-button ${activeTab === "calendar" ? "active" : ""}`}
          onClick={() => setActiveTab("calendar")}
        >
          Calendar
        </button>
        <button
          className={`tab-button ${activeTab === "list" ? "active" : ""}`}
          onClick={() => setActiveTab("list")}
        >
          List
        </button>
      </div>

      {/* 조건부 렌더링 */}
      {activeTab === "calendar" && (
        <>
          <div className="blog-container">
            <div className="search-section">
              <input
                type="text"
                className="search-input"
                placeholder="검색어를 입력하세요"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <hr />
            <div className="search-results">
              {/* {filteredEntries.length > 0 ? (
                filteredEntries.map((entry) => (
                  <Link
                    key={entry.id}
                    to={`/diary/${entry.owner_id}/${
                      entry.scheduled_at.split("T")[0]
                    }/${entry.id}`}
                    state={{
                      entryTitle: entry.title,
                      id: entry.id,
                      entryOwner: entry.owner_id,
                    }}
                    style={{ textDecoration: "none" }}
                  >
                    <div className="blog-entry">
                      <h3 className="entry-title1">{entry.title}</h3>
                      <h3 className="entry-date">
                        {entry.created_at.split("T")[0]}
                      </h3>
                    </div>
                  </Link>
                ))
              ) : (
                <p>검색 결과가 없습니다.</p>
              )} */}
            </div>
          </div>
        </>
      )}

      {activeTab === "list" && (
        <div className="list-container">
          <h2>나의 일기장 목록</h2>
          <table className="list-table">
            <tbody>
              {diaryEntries.map((entry, i) => (
                <tr className="list-item" key={i}>
                  <td className="icon-column">{i + 1}.</td>
                  <Link
                    to={`/diary/${user_id}/${entry.date}/${entry.id}`}
                    state={{ entryTitle: entry.title }}
                    style={{ textDecoration: "none" }}
                  >
                    <td className="title-column">{entry.title}</td>
                  </Link>
                  <td className="date-column">
                    {entry.scheduled_at &&
                      new Date(entry.scheduled_at).toISOString().split("T")[0]}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Admin;
