import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useTab } from "./TabContext";
import axios from "axios";
import "./Admin.css";

const Admin = () => {
  const navigate = useNavigate();
  const { activeTab, setActiveTab } = useTab();
  const [userList, setUserList] = useState([
    { id: 1, username: "진하영", email: "wls@gmail.com" },
    { id: 2, username: "류지예", email: "ryu@naver.com" },
    { id: 3, username: "박은지", email: "park@gmail.com" },
    { id: 4, username: "백현빈", email: "hyeon@daum.com" },
    { id: 5, username: "조경호", email: "cho@naver.com" },
  ]);

  return (
    <div className="admin">
      {/* 탭 영역 */}
      <div className="tab-header">
        <button
          className={`tab-button ${activeTab === "Account" ? "active" : ""}`}
          onClick={() => setActiveTab("Account")}
        >
          Account
        </button>
        <button
          className={`tab-button ${activeTab === "Blog" ? "active" : ""}`}
          onClick={() => setActiveTab("Blog")}
        >
          Blog
        </button>
      </div>

      {/* 조건부 렌더링 */}
      {activeTab === "Account" && (
        <>
          <div className="list-container">
            <h2>사용자 계정 목록</h2>
            <table className="list-table">
              <tbody>
                {userList.map((el, i) => (
                  <tr className="list-item" key={el.id}>
                    <td className="icon-column">{i + 1}.</td>
                    {/* <Link
                      to={`/diary/${user_id}/${entry.date}/${entry.id}`}
                      state={{ entryTitle: entry.title }}
                      style={{ textDecoration: "none" }}
                    > */}
                    <td className="title-column">{el.username}</td>
                    <td className="title-column">{el.email}</td>
                    {/* </Link> */}
                    {/* <td className="date-column">
                      {entry.scheduled_at &&
                        new Date(entry.scheduled_at)
                          .toISOString()
                          .split("T")[0]}
                    </td> */}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}

      {activeTab === "Blog" && (
        <div className="list-container">
          <h2>나의 일기장 목록</h2>
          <table className="list-table">
            <tbody>
              {/* {diaryEntries.map((entry, i) => (
                <tr className="list-item" key={i}>
                  <td className="icon-column">{i + 1}.</td>
                  <Link
                    to={`/diary/${user_id}/${entry.date}/${entry.id}`}
                    state={{ entryTitle: entry.title }}
                    style={{ textDecoration: "none" }}
                  >
                    <td className="title-column">{entry.title}</td>
                  </Link>
                  <td className="date-column">
                    {entry.scheduled_at &&
                      new Date(entry.scheduled_at).toISOString().split("T")[0]}
                  </td>
                </tr>
              ))} */}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Admin;
