import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom"; // useNavigate는 BrowserRouter 내에서만 사용 가능
import Navbar from "./Navbar";
import NavbarAdm from "./NavbarAdm";
import Admin from "./Admin";
import Signin from "./Signin";
import Signup from "./Signup";
import Calendar from "./Calendar";
import Diary from "./Diary";
import Blog from "./Blog";
import TabProvider from "./TabProvider";
import axios from "axios";

const App = () => {
  const [is_admin, setIsAdmin] = useState(null);
  const navigate = useNavigate(); // useNavigate는 BrowserRouter 안에서만 사용 가능

  useEffect(() => {
    const storedIsAdmin = localStorage.getItem("is_admin");
    if (storedIsAdmin !== null) {
      setIsAdmin(JSON.parse(storedIsAdmin)); // localStorage에서 is_admin 값을 가져와 상태에 저장
    }
  }, []);

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post("http://localhost:8000/user/Signin", {
        email,
        password,
      });

      localStorage.setItem("token", response.data.access_token);
      localStorage.setItem("user_id", response.data.user_id);
      localStorage.setItem("is_admin", response.data.is_admin);

      setIsAdmin(response.data.is_admin);

      response.data.is_admin === true
        ? navigate("/admin")
        : navigate("/calendar");
    } catch (err) {
      console.error(err);
      alert("로그인에 실패했습니다. 다시 시도해주세요.");
    }
  };

  if (is_admin === null) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <TabProvider>
        <BrowserRouter>
          {is_admin ? <NavbarAdm /> : <Navbar />}
          <Routes>
            {is_admin ? (
              <Route path="/admin" element={<Admin />} />
            ) : (
              <>
                <Route path="/" element={<Signin />} />
                <Route path="/signup" element={<Signup />} />
                <Route path="/signin" element={<Signin />} />
                <Route path="/calendar" element={<Calendar />} />
                <Route path="/diary/:user_id/:date" element={<Diary />} />
                <Route
                  path="/diary/:user_id/:date/:title"
                  element={<Diary />}
                />
                <Route path="/blog" element={<Blog />} />
              </>
            )}
          </Routes>
        </BrowserRouter>
      </TabProvider>
    </>
  );
};

export default App;
